<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = "osc_sys_manager_menu";		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$cardUpd = array(					'name'	=> $_POST['name'],					'link'		=> $_POST['link'],					'block'		=> $_POST['block'][0],					'target'		=> $_POST['target'][0],					'created'		=> date("Y-m-d H:i:s", time()),					'modified'		=> date("Y-m-d H:i:s", time())	);		// Create main table item		$query = "INSERT INTO `$appTable` ";		$fieldsStr = " ( ";		$valuesStr = " ( ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;				$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");				$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");	}		$fieldsStr .= " ) ";		$valuesStr .= " ) ";		$query .= $fieldsStr." VALUES ".$valuesStr;			$item_id = $ah->rs($query,0,1,1);	if($item_id)	{		$data['item_id'] = $item_id;	}else	{		$data['item_id'] = 0;	}	$data['real_item_id'] = $item_id;			$data['message'] = "Пункт меню создан. ID =  $item_id";	$data['status'] = "success";	