<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];	$slider_id = $_POST['section_slider_id'];	$lpx = $_POST['lpx'];	$lang_prefix = ($lpx ? $lpx."_" : ""); // empty = iw	$now = date("Y-m-d H:i:s", time());	$caption = $_POST['section_caption'];	$caption = str_replace(' ', ' <br>', $caption);		$cardUpd = array(						'section_caption'			=> $caption,						'section_sub_caption'			=> $_POST['section_sub_caption'],						'section_content'			=> $_POST['section_content'],						'modified'	=> $now					);						// File upload filename	$file_path = "../../../../split/files/home_slides/";	$filename = "slides";		if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0) {				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"hm_sl_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>1920,				'resize_h'		=>1080,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload){			$now = date("Y-m-d H:i:s");			foreach($files_upload as $file_upload){				$query = "INSERT INTO `osc_slides` (`filename`, `slider_id`, `created`, `modified`, `type`) VALUES ('$file_upload', '$slider_id', '$now', '$now', 1)";				$ah->rs($query);				chmod($file_path.$file_upload,0777);			}		}	}		$query = "UPDATE `osc_page_home` SET ";					$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd) {		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";	$ah->rs($query);					$data['message'] = "Пункт Меню успешно сохранен.";		