<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = "osc_sys_manager_menu";		$item_id = $_POST['item_id'];	$cardUpd = array(					'name'	=> $_POST['name'],					'link'		=> $_POST['link'],					'block'		=> $_POST['block'][0],					'target'		=> $_POST['target'][0],										'modified'		=> date("Y-m-d H:i:s", time())					);						// File upload 			// Update main table		$query = "UPDATE `$appTable` SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query,0,1);		$data['status'] = "success";	$data['message'] = "Пункт меню обновлен.";	